import React from "react";
import "./Hero.css";
import anime from 'animejs/lib/anime.es';

const Hero = () => {

  const test = document.querySelector('.big');
  const fly = () => {
    const anime = require('animejs');
    anime({
      targets: test,
      scale: {
        delay: 800,
        value: 1.5
      },
      duration: 1500
    });
  }

  return (
    <div className="hero">
      <div className="heading">
        <h1 onClick={fly} className="big">Hello, I'm Wasif.</h1>

        <h3 style={{ textAlign: "center" }}>Stay Hungry Stay Foolish.</h3>
        <h3 style={{fontSize: "1rem"}}>-Steve Jobs</h3>
        <br />
        <h2 style={{fontWeight: "300"}}>Welcome to my Portfolio.</h2>
      </div>

    </div>
  );
};

export default Hero;
