import React from "react";
import "./Hero.css";
import {
  AnimatePresence,
  motion,
  useViewportScroll,
  useTransform,
} from "framer-motion/dist/framer-motion";
import { Col, Row } from "react-bootstrap";

const Hero = () => {
  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [0, 1], [0.2, 1]);
  const scaler = useTransform(scrollYProgress, [0, 1], [0.2, 1]);
  return (
    <div className="hero">
      <div className="heading">
        <h1 className="big">Hello, I'm Wasif.</h1>

        <h3 style={{ textAlign: "center" }}>Stay Hungry Stay Foolish.</h3>
        <h3 style={{ fontSize: "1rem" }}>-Steve Jobs</h3>
        <br />
        <h2 style={{ fontWeight: "300" }}>Welcome to my Portfolio.</h2>
      </div>
      <div className="motion-background">
        <Row>
          <Col xs={12} lg={4} className="d-flex justify-content-center">
            <div>
              <motion.div
                style={{
                  scale,
                  backgroundColor: "white",
                  borderRadius: "50%",
                  width: "200px",
                  height: "200px",
                }}
              >
                <motion.div
                  style={{
                    scaleY: scrollYProgress,
                  }}
                />
              </motion.div>
            </div>
            <div>
              <motion.div
                style={{
                  scaler,
                  backgroundColor: "white",
                  borderRadius: "50%",
                  width: "200px",
                  height: "200px",
                }}
              >
                <motion.div
                  style={{
                    scaleY: scrollYProgress,
                  }}
                />
              </motion.div>
            </div>
          </Col>
          <Col xs={12} lg={4} className="d-flex justify-content-center">
            <div>
              <motion.div
                style={{
                  scale,
                  backgroundColor: "white",
                  borderRadius: "50%",
                  width: "200px",
                  height: "200px",
                }}
              >
                <motion.div
                  style={{
                    scaleY: scrollYProgress,
                  }}
                />
              </motion.div>

            </div>
            <div>
              <motion.div
                style={{
                  scaler,
                  backgroundColor: "white",
                  borderRadius: "50%",
                  width: "200px",
                  height: "200px",
                }}
              >
                <motion.div
                  style={{
                    scaleY: scrollYProgress,
                  }}
                />
              </motion.div>

            </div>
          </Col>
          <Col xs={12} lg={4} className="d-flex justify-content-center">
            <div>
              <motion.div
                style={{
                  scale,
                  backgroundColor: "white",
                  borderRadius: "50%",
                  width: "200px",
                  height: "200px",
                }}
              >
                <motion.div
                  style={{
                    scaleY: scrollYProgress,
                  }}
                />
              </motion.div>
            </div>
            <div>
              <motion.div
                style={{
                  scaler,
                  backgroundColor: "white",
                  borderRadius: "50%",
                  width: "200px",
                  height: "200px",
                }}
              >
                <motion.div
                  style={{
                    scaleY: scrollYProgress,
                  }}
                />
              </motion.div>

            </div>
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default Hero;
