import React from "react";
import { Row, Button, Container, Carousel, Image } from "react-bootstrap";
import Particles from "react-tsparticles";
import Hero from "../components/Hero";

import "./Home.css";

const Home = () => {
  const particlesInit = (main) => {
    console.log(main);

    // you can initialize the tsParticles instance (main) here, adding custom shapes or presets
  };

  const particlesLoaded = (container) => {
    console.log(container);
  };
  return (
    <div>
      <Hero />
      <Particles id="tsparticles" init={particlesInit} loaded={particlesLoaded} options={{
        fpsLimit: 30,
        interactivity: {
          events: {
            onClick: {
              enable: true,
              mode: "push",
            },
            onHover: {
              enable: true,
              mode: "repulse"
            },
            resize: true,
          },
          modes: {
            bubble: {
              distance: 400,
              duration: 2,
              opacity: 0.8,
              size: 60,
            },
            push: {
              quantity: 4,
            },
            repulse: {
              distance: 50,
              duration: 0.6,
            },
          },
        },
        particles: {
          color: {
            value: "#D4AF37",
          },
          links: {
            color: "#ffffff",
            distance: 150,
            enable: true,
            opacity: 0.5,
            width: 1,
          },
          collisions: {
            enable: true,
          },
          move: {
            direction: "none",
            enable: true,
            outMode: "bounce",
            random: false,
            speed: 2,
            straight: false,
          },
          number: {
            density: {
              enable: true,
              value_area: 200,
            },
            value: 10,
          },
          opacity: {
            value: 0.5,
          },
          shape: {
            type: "edge",
          },
          size: {
            random: true,
            value: 10,
          },
        },
        detectRetina: true,
      }}/>
      <Container
        fluid
        style={{
          backgroundColor: "black",
          justifyContent: "center",
          padding: "8%",
        }}
      >
        <Carousel>
          <Carousel.Item>
            <Image
              style={{ maxWidth: "70%" }}
              className="mx-auto d-block"
              src="../assets/weather.png"
              alt="First slide"
              rounded
            />
            <Carousel.Caption>
              <h3>Weather App</h3>
              <p>Shows you weather data based on the current location</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <Image
              style={{ maxWidth: "70%" }}
              className="mx-auto d-block"
              src="../assets/quarters.png"
              alt="Second slide"
            />

            <Carousel.Caption>
              <h3>Responsive React App</h3>
              <p>Showcases responsiveness across all screen sizes</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <Image
              style={{ maxWidth: "70%" }}
              className="mx-auto d-block"
              src="../assets/pokemon.png"
              alt="Third slide"
            />

            <Carousel.Caption>
              <h3>Pokedex</h3>
              <p>Learn Pokemon facts here</p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
        <Row
          gutter={[24, 24]}
          style={{ justifyContent: "center", padding: "50px" }}
        >
          <Button
            href="/projects"
            variant="outline-light"
            size="lg"
            style={{ width: "30%" }}
          >
            See my work
          </Button>
        </Row>
      </Container>
    </div>
  );
};

export default Home;
