          </Col>
          <Col>
            <Button
              variant="dark"
              value="Add"
              type="submit"
              onClick={submitHandler}
            >
              Add
            </Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
};

export default Todolist;
