export const drawBall = (ctx, x, y, r)=>{

    // ctx.beginPath();
    // ctx.arc(x, y, r, 0, 3*Math.PI)
    // ctx.fillStyle = "aqua"
    // ctx.fill()

    ctx.beginPath();
    ctx.arc(x, y, r, 0, Math.PI * 2, true); // Outer circle
    ctx.moveTo(110, 75);
    ctx.arc(x, y, r, 0, Math.PI, false);  // Mouth (clockwise)
    ctx.moveTo(65, 65);
    ctx.arc(x, y, r, 0, Math.PI * 2, true);  // Left eye
    ctx.moveTo(95, 65);
    ctx.arc(x, y, r, 0, Math.PI * 2, true);  // Right eye
    ctx.strokeStyle = "white";
    ctx.stroke();



}
