import React from "react";
import "./Hero.css";
import { AnimatePresence, motion } from "framer-motion/dist/framer-motion";

const Hero = () => {
  return (
    <div className="hero">
      <div className="heading">
        <h1 className="big">Hello, I'm Wasif.</h1>

        <h3 style={{ textAlign: "center" }}>Stay Hungry Stay Foolish.</h3>
        <h3 style={{ fontSize: "1rem" }}>-Steve Jobs</h3>
        <br />
        <h2 style={{ fontWeight: "300" }}>Welcome to my Portfolio.</h2>
      </div>
      <div className="motion-background">
        <div style={{textAlign: "center", width: "50%", margin: "auto"}}> 
          <motion.div
          style={{
            width: "35px",
            height: "35px",
            backgroundColor: "white",
            borderRadius: "10%",
            textAlign: "center",
          }}
          animate={{ x: [0, 225, 0] }}
          transition={{ ease: "easeOut", duration: 2 }}
        />
        </div>
        <div style={{textAlign: "right", width: "50%", margin: "auto"}}> 
          <motion.div
          style={{
            width: "35px",
            height: "35px",
            backgroundColor: "white",
            borderRadius: "10%",
            textAlign: "center",
          }}
          animate={{ x: [225, 0, 225] }}
          transition={{ ease: "easeOut", duration: 2 }}
        />
        </div>

      </div>
    </div>
  );
};

export default Hero;
