import axios from "axios";
import React, {useEffect} from "react";
import {Link} from "react-router-dom";

const MediumBlog = () => {
  mediumURL =
    "https://api.rss2json.com/v1/api.json?rss_url=https://medium.com/feed/@your-medium-username";

    this.state = {
        profile: {
          ptitle: "",
          name: "",
          avatar: "",
          profileurl: "",
        },
        item: [],
        isloading: true,
        error: null
      };
    useEffect(() => {
        axios.get(this.mediumURL).then((data) => {
            console.log(data);
        })
  },[])

  return (props, p) => {
      <div className={`col-md-4 col-sm-6 col-xs-12 ${c.grid}`}>
      <div className>
        <div

          style={{ backgroundImage: `url(${props.thumbnail})` }}
        >

          <div >
            <a
              href={props.profileurl}
              rel="noopener noreferrer"
              target="_blank"
              style={{ backgroundImage: `url(${props.avatar})` }}
            >
              Written By Mehul
            </a>
          </div>
        </div>

        <div>
          <h5 >

            <Link
              to={props.title}


            >
              {props.title}
            </Link>
          </h5>

          <p>{`${ToText(
            props.description.substring(0, 1000)
          )}...`}</p>
          <br />

           {props.author}

          <br />
          <span >
            {moment(props.pubDate).format("MMM DD, YYYY hh:mm")}
          </span>
        </div>
      </div>

  </div>;
};

};

export default MediumBlog;
