export const drawBall = (ctx, x, y, r)=>{

    // ctx.beginPath();
    // ctx.arc(x, y, r, 0, 3*Math.PI)
    // ctx.fillStyle = "aqua"
    // ctx.fill()

    ctx.beginPath();
    ctx.arc(x, y, r, 0, Math.PI * 2, true); // Outer circle
    ctx.fillStyle = `rgb(
        0,
        ${Math.floor(255 - 42.5)},
        ${Math.floor(100 - 82.5)})`;
    ctx.fill();



}
