import React from "react";
import {Form, Button, Row, Col} from 'react-bootstrap';

const TaskForm = () => {
  return (
    <div>
      <Form>
        <Form.Label style={{ fontSize: "2rem", fontWeight: "lighter" }}>
          Task Master
        </Form.Label>
        <Row style={{ padding: "5%" }}>
          <Col xs={9}>
            <Form.Control
              type="text"
              placeholder="Add Task"
              onChange={inputTextHandler}
            />
          </Col>
          <Col>
            <Button
              variant="dark"
              value="Add"
              type="submit"
              onClick={submitHandler}
            >
              Add
            </Button>
          </Col>
        </Row>
        <Row></Row>
      </Form>
    </div>
  );
};

export default TaskForm;
