import React from "react";
import "./Hero.css";
import { AnimatePresence, motion, useViewportScroll, useTransform } from "framer-motion/dist/framer-motion";

const Hero = () => {
  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [0, 1], [0.2, 2]);
  return (
    <div className="hero">
      <div className="heading">
        <h1 className="big">Hello, I'm Wasif.</h1>

        <h3 style={{ textAlign: "center" }}>Stay Hungry Stay Foolish.</h3>
        <h3 style={{ fontSize: "1rem" }}>-Steve Jobs</h3>
        <br />
        <h2 style={{ fontWeight: "300" }}>Welcome to my Portfolio.</h2>
      </div>
      <div className="motion-background">
        <div style={{ textAlign: "center", width: "50%", margin: "auto" }}>
          <motion.div style={{ scale, backgroundColor: "white", borderRadius: "3%"}}>
            <motion.div
              style={{
                scaleY: scrollYProgress,
              }}
            />
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default Hero;
