import React, { useState } from "react";
import { Row, Col, Image, Card, Container, Form, Button } from "react-bootstrap";
import Todolist from "../components/Playground/Todolist/Todolist";
import Speech from "../components/Speech";
import Weather from "../components/Weather";
const Playground = () => {
  // chatgpt container handle
  const [inputValue, setInputValue] = useState("");
  const [outputValue, setOutputValue] = useState("");


  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleButtonClick = async () => {
    try {
      const response = await fetch('/api/openai', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text: inputValue }),
      });

      const data = await response.json();
      setOutputValue(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div
      style={{
        background:
          "linear-gradient(90deg, rgba(26,26,27,1) 16%, rgba(40,40,40,1) 50%, rgba(19,19,19,1) 84%)",
      }}
    >
      <Container>
        <Row>
          <Col
            xs={12}
            className="d-flex justify-content-center"
            style={{
              textAlign: "center",
              paddingTop: "5%",
              paddingBottom: "5%",
            }}
          >
            <Card style={{ width: "60rem", padding: "2rem" }} bg="success" text="white" border="light">
              <Card.Body>
                <Card.Title>Fire your thoughts</Card.Title>
                <Form style={{padding: "1rem"}}>
                  <Form.Group controlId="inputField" style={{padding: "1rem"}}>
                    <Form.Control
                      type="text"
                      value={inputValue}
                      onChange={handleInputChange}
                      style={{padding: "1rem"}}
                    />
                  </Form.Group>
                  <Button variant="primary"  onClick={handleButtonClick}>

                    Submit
                  </Button>
                </Form>
                <Card.Text className="mt-3" style={{padding: "1rem"}}>Output: {outputValue}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col
            xs={12}
            className="d-flex justify-content-center"
            style={{
              textAlign: "center",
              paddingTop: "5%",
              paddingBottom: "5%",
            }}
          >
            <Card style={{ width: "60rem" }} bg="light">
              <Weather />
            </Card>
          </Col>
        </Row>
        <Row>
          <Col
            xs={12}
            className="d-flex justify-content-center"
            style={{
              textAlign: "center",
              paddingTop: "5%",
              paddingBottom: "5%",
            }}
          >
            <Card style={{ width: "30rem" }} bg="light" border="dark">
              <Todolist />
            </Card>
          </Col>
        </Row>
        <Row>
          <Col
            xs={12}
            className="d-flex justify-content-center"
            style={{
              textAlign: "center",
              paddingTop: "5%",
              paddingBottom: "5%",
            }}
          >
            <Card style={{ width: "60rem" }} bg="light" border="dark">
              <Speech />
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Playground;
