import React, { useEffect, useState, useRef } from "react";
// https://github.com/tensorflow/tfjs-models/tree/master/speech-commands

// 0. Import depdendencies
import * as tf from "@tensorflow/tfjs";
import * as speech from "@tensorflow-models/speech-commands";
import "./Speech.css";
// 4. Draw Ball
import { drawBall } from "./utilities";

const Speech = () => {
  // 1. Create model and action states
  const [model, setModel] = useState(null);
  const [action, setAction] = useState(null);
  const [labels, setLabels] = useState(null);

  // 6. Create Canvas Ref and x,y,r
  const canvasRef = useRef(null);
  const [x, setX] = useState(300);
  const [y, setY] = useState(300);
  const [r, setR] = useState(10);

  // 2. Create Recognizer
  const loadModel = async () => {
    const recognizer = await speech.create("BROWSER_FFT");
    console.log("Model loaded");
    await recognizer.ensureModelLoaded();
    console.log(recognizer.wordLabels());
    setModel(recognizer);
    setLabels(recognizer.wordLabels());
  };

  useEffect(() => {
    loadModel();
  }, []);

  function argMax(arr) {
    return arr.map((x, i) => [x, i]).reduce((r, a) => (a[0] > r[0] ? a : r))[1];
  }

  const pause = async () => {
    model.stopListening();
    console.log("Stopped recording from inside pause");
  };

  const recognizeCommand = async () => {
    model.listen(
      (result) => {
        console.log("Started listening");
        console.log(result.scores);
        setAction(labels[argMax(Object.values(result.scores))]);
        console.log({ action });
      },
      {
        includeSpectrogram: true,
        probabilityThreshold: 0.9,
      }
    );
  };

  // // 7. Update ball state
  // const numberMap = {
  //   "zero":0,
  //   "one":1,
  //   "two":2,
  //   "three":3,
  //   "four":4,
  //   "five":5,
  //   "six":6,
  //   "seven":7,
  //   "eight":8,
  //   "nine":9
  // }

  // useEffect(()=>{
  //   // Update position x,y
  //   const update = action === 'up' ? setY(y-10) : action==="down" ? setY(y+10) : action==="left" ? setX(x-10) : action==="right"? setX(x+10) : ""
  //   // Update size r
  //   if(Object.keys(numberMap).includes(action)){
  //     setR(10*numberMap[action])
  //   }

  //   canvasRef.current.width = 600;
  //   canvasRef.current.height = 600;
  //   const ctx = canvasRef.current.getContext('2d')
  //   console.log(x,y,r)
  //   drawBall(ctx,x,y,r)
  //   setAction('base')
  // }, [action])

  // // 3. Listen for Actions
  // function argMax(arr){
  //   return arr.map((x, i) => [x, i]).reduce((r, a) => (a[0] > r[0] ? a : r))[1];
  // }

  // const recognizeCommands = async () =>{
  //   console.log('Listening for commands')
  //   model.listen(result=>{
  //     // console.log(labels[argMax(Object.values(result.scores))])
  //     setAction(labels[argMax(Object.values(result.scores))])

  //   }, {includeSpectrogram:true, probabilityThreshold:0.9})
  //   // setTimeout(()=>model.stopListening(), 10e3)
  // }

  return (
    <div style={{ textAlign: "center", margin: "15%" }}>
      {/* 5. Setup Canvas */}
      <canvas 
        ref={canvasRef}
        style={{
          textAlign: "center",
          zIndex: "9",
          width: 640,
          height: 300,
          backgroundColor: "gold",
          marginBottom: "10%"
        }}
        />
      <div style={{ display: "flex", columnGap: "15%", textAlign: "center", alignItems: "center"}}>
        <button className="control" onClick={recognizeCommand}>
          Talk
        </button>
        <button className="control" style={{backgroundColor:"red"}} onClick={pause}>
          Stop
        </button>
        {action ?  <h3 style={{ padding: "5%"}}>{action}</h3> : <h3 style={{ padding: "5%" }}>Nothing detected</h3>}
        </div>
        <div style={{textAlign: "left", marginTop: "7%"}}>
            <h2 style={{ marginBottom: "3%"}}>Instructions</h2>
            <h3 style={{ fontWeight: "400", marginBottom: "1%"}}>1. Press Talk and say commands like "one", "up", "go", "stop" etc</h3>
            <h3 style={{ fontWeight: "400" }}>2. Press Stop to disengage</h3>
        </div>
    </div>
  );
};

export default Speech;
