import React, { useEffect, useState } from "react";
import { Row, Col, Card, Button, FloatingLabel, Form } from "react-bootstrap";

const Weather = () => {
  const apiKey = "86ddf68890ff862711c0fc76d79c9b6e";
  const [weatherData, setWeatherData] = useState([{}]);
  const [city, setCity] = useState("");
    const [location, setLocation] = useState({
        loaded: false,
        coordinates: { lat: "", lng: ""}
    })

    const success = location => {
        // setLocation({ 
        //     loaded: true,
        //     lat: location.coords.latitude, lng: location.coords.longitude})
        console.log(location);

        fetch(`api.openweathermap.org/data/2.5/weather?lat=${location.coords.latitude}&lon=${location.coords.longitude}&appid=${apiKey}`)
                .then((response) => response.json())
                .then((data) => setWeatherData(data));
                console.log(weatherData);

    }

    const failed = error => {
        setLocation({
            loaded: true,
            error,
        })
        console.log("reached failure");
    }

    useEffect(() => {
        if (!("geolocation" in navigator)){
            failed({
                code: 0,
                message: "Geolocation not supported"
            })

            }

    }, []);

//   

  return (
    <div>
      <Row s={1} md={2}>
        <Col>
          <Card style={{}}>
            <Card.Body>
              <Col>
                <Card.Title>How's the weather?</Card.Title>
                <Button onClick={() => navigator.geolocation.getCurrentPosition(success, failed)}>Check</Button>
              </Col>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default Weather;
